mapper.select:
SELECT 

FROM  a




		AND a.phone = #{user.phone}


		AND a.name LIKE concat('%',#{user.name},'%')


		AND a.auth = #{user.auth}


		AND a.password = #{user.password}


ORDER BY a.id ASC

