mapper.select:
SELECT 

FROM  a




		AND a.createid = #{word.createid}


		AND a.replyid = #{word.replyid}


ORDER BY a.id ASC

mapper.update:
UPDATE  SET 	
	content = #{content},
	createtime = #{createtime},
	createid = #{createid},
	createname = #{createname}
WHERE id = #{id, jdbcType = VARCHAR}

UPDATE  SET 	
	reply = #{reply},
	replytime = #{replytime},
	replyid = #{replyid},
	replyname = #{replyname}
WHERE id = #{id, jdbcType = VARCHAR}
